import { CartActionTypes } from './cart.types';

export const toggleHiddenCart = () => ({
    type: CartActionTypes.TOGGLE_HIDDEN_CART
});

export const addItems = (item) => ({
    type: CartActionTypes.ADD_ITEM,
    payload: item
});
